import React, {useState} from 'react';
import { makeStyles } from '@material-ui/styles';
import {
    Card,
    CardContent,
    Button,
    Checkbox,
    FormControlLabel,
    TextField,
    Box,
    Collapse,
    Grid,
    Slider,
    Typography,
    List,
    ListSubheader,
    ListItem,
    ListText,
    ListItemText,
    Paper,
    ListItemSecondaryAction,
    IconButton
  } from '@material-ui/core';
  import { UncontrolledCollapse, CardBody} from 'reactstrap'

  import {Autocomplete} from '@material-ui/lab';

  import DeleteIcon from '@material-ui/icons/Delete';

  class Filter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          isGoing: true,
          numberOfGuests: 2,
          Tester: false,
          toggleFilters: false,
          range: [0,100],
          itemSearchText: "",
          groceryList : ["Stawberries"]
        };

        this.handleInputChange = this.handleInputChange.bind(this);
      }



    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
        console.log(this.state)
    }   

    itemSearchTextChanged = (event) => {
        this.setState({itemSearchText: event.target.value})
    }

    render(){
        const useStyles = makeStyles(theme => ({
            root: {
                paddingTop: 56,
            },
            stickyList: {
                width: '100%',
                maxWidth: 360,
                backgroundColor: theme.palette.background.paper,
                position: 'relative',
                overflow: 'auto',
                maxHeight: 300,
            },
            typeFilter : {
                width: 300,
            }
        }
        ));

        const handleChange2 = (event, newValue) => {
            this.setState({...this.state, ['range'] : newValue})
        };
        const defaultProps = {
            options: top100Films,
            getOptionLabel: option => option.title,
        };
        const itemSearchProps = {
            options: groceryItemList,
            getOptionLabel: option => option.title,
        };
        const flatProps = {
            options: top100Films.map(option => option.title),
        };
        const classes = useStyles;
        const handleChange = name => event => {
            this.setState({ ...this.state, [name]: event.target.checked });
            console.log(this.state, event.target)
        };
        const toggleMoreFilters = () =>{
            var toggle = this.state.toggleFilters;
            this.setState({...this.state, ['toggleFilters']:!toggle})
            console.log(this.state)
        }

        return(
            <Card style={{padding: 30, paddingTop: 0}} className={classes.root}>
