import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { Amenities } from '..';
import { Grid } from '@material-ui/core';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    color: theme.palette.primary,
  },
}));

export default function TabBar() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <div className={classes.root}>
      <AppBar position="static" style={{backgroundColor:"white", color:'#3F51B5'}}>
        <Grid direction="row" container alignItems='center' >
            <Grid item lg={3} style={{backgroundColor:"blue"}}>
            <Tabs value={value} onChange={handleChange} aria-label="simple tabs example">
                <Tab label="Applications" {...a11yProps(0)} />
                <Tab label="Wishlist" {...a11yProps(1)} />
                <Tab label="Profile" {...a11yProps(2)} />
            </Tabs>
            </Grid>
        </Grid>

      </AppBar>
      <TabPanel value={value} index={0} style={{backgroundColor:'#F5F6F8'}}>
        <Grid direction="column" container alignItems='center'>
            <Grid item lg={12}>
                <Amenities style={{width:900}}/>
            </Grid>
        </Grid>

      </TabPanel>

      <TabPanel value={value} index={1}>
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>
    </div>
  );
}
