import React, {Component} from 'react';
import { makeStyles } from '@material-ui/styles';
import { Grid, Hidden, Container, IconButton} from '@material-ui/core';
import windowSize from 'react-window-size';
import {
  Budget,
  TotalUsers,
  TasksProgress,
  TotalProfit,
  LatestSales,
  UsersByDevice,
  LatestProducts,
  LatestOrders,
  Filter,
  PropertyList,
  MapContainer
} from './components';
import './PropertySearch.css'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux';
import SearchIcon from '@material-ui/icons/Search';

const MODAL_OPEN_CLASS = "body--modal-open";
makeStyles(theme => (
  {
  root: {
    padding: theme.spacing(0), width: 800, 
  }
}));
const isDesktop = 0;

class PropertySearch extends Component {

  componentDidMount() {
    document.body.classList.add(MODAL_OPEN_CLASS);
  }

  componentWillUnmount() {
    document.body.classList.remove(MODAL_OPEN_CLASS);
  }

  render(){
    const {apartments} = this.props;
    console.log('Redux Test: ', apartments)
    if(isDesktop == 0){
      return (
        <div>
            <Hidden smDown>
              <div style={{maxWidth:800}}>
                <div style={{display:'flex', overflow:'hidden', overflowX: 'hidden'}}>
                  <div className={makeStyles.root} style={{maxHeight: this.props.windowHeight - 75, overflow: 'auto', width:800}}>
                    <Filter style={{padding:0}}></Filter>
                    <Container style={{padding:20, width:'100%'}}>
                      <PropertyList apartments={apartments}></PropertyList>
                    </Container>
                  </div>
                  <div>
                    <MapContainer height={this.props.windowHeight-60} width={this.props.windowWidth-800}></MapContainer>
                  </div>
                </div>
                </div>
            </Hidden>
            <Hidden mdUp>
              <div style={{height:60, width:'100%', backgroundColor:'#3F51B5', position:'fixed', bottom:0}}>
                <Grid container>
                    <Grid item>
                      <IconButton>
                        <SearchIcon color="white"/>
                      </IconButton>
                    </Grid>
                </Grid>
              </div>
            </Hidden>
        </div>




        );
    } else {
        return(
            <div></div>
        );
    }
  };
  }

  const mapStateToProps = (state, ownProps) => {
    console.log('stateOftheUnion: ', state)
    return {
      apartments: state.firestore.ordered.apartments
    }
  }

export default compose(
  connect(mapStateToProps),
  firestoreConnect([
    {collection: 'apartments'}
  ])
) (windowSize(PropertySearch));
